#define GLSLIFY 1
#endif

uniform vec2 u_resolution;
uniform vec2 u_mouse;
uniform float u_time;

void main(){
    vec2 st=gl_FragCoord.xy/u_resolution.xy;
    st.x*=u_resolution.x/u_resolution.y;

    vec3 color=vec3(0.);
    color=vec3(st.x,st.y,abs(sin(u_time)));

    gl_FragColor=vec4(color,1.);
}

#ifdef GL_ES
precision mediump float;
#define GLSLIFY 1
#endif

uniform vec2 u_resolution;
uniform vec2 u_mouse;
uniform float u_time;

uniform sampler2D pic; // /static/5477ce484b67607df12a074de62c372a/profile.jpg

float p(float n1,float n2,float th){
    return n1+(n2-n1)*th;
}

vec4 slide(vec4 c1,vec4 c2,float th){
    return vec4(
        p(c1.r,c2.r,th),
        p(c1.g,c2.g,th),
        p(c1.b,c2.b,th),
        p(c1.a,c2.a,th)
    );
}

void main(){
    vec2 st=gl_FragCoord.xy/u_resolution.xy;
    vec4 c=texture2D(pic,st);
    vec4 avg=vec4((c.r+c.g+c.b+c.a)/3.5);
    gl_FragColor=slide(c,avg,abs(sin(u_time)));
}
